import React, { useState, useEffect } from "react";
import { format, parseISO } from "date-fns";

const API_URL = "http://localhost:8000";

function EventManagement() {
  const [events, setEvents] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    date: "",
    location: "",
  });
  const [editingId, setEditingId] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchEvents = async () => {
    setLoading(true);
    try {
      const response = await fetch(`${API_URL}/events`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setEvents(data);
    } catch (err) {
      setError(`Failed to load events: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchEvents();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      // Validate all fields
      if (!formData.name || !formData.date || !formData.location) {
        throw new Error("All fields are required");
      }

      // Format date for backend
      const formattedDate = new Date(formData.date).toISOString();

      const requestOptions = {
        method: editingId ? "PUT" : "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
        body: JSON.stringify({
          name: formData.name,
          date: formattedDate,
          location: formData.location,
        }),
      };

      const url = editingId
        ? `${API_URL}/events/${editingId}`
        : `${API_URL}/events`;

      const response = await fetch(url, requestOptions);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Request failed");
      }

      const result = await response.json();

      // Reset form and refresh
      setFormData({ name: "", date: "", location: "" });
      setEditingId(null);
      await fetchEvents();
    } catch (err) {
      setError(err.message);
      console.error("Submission error:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (event) => {
    // Convert ISO date to local datetime format for input
    const localDate = format(parseISO(event.date), "yyyy-MM-dd'T'HH:mm");
    setFormData({
      name: event.name,
      date: localDate,
      location: event.location,
    });
    setEditingId(event.id);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this event?")) return;

    setLoading(true);
    try {
      const response = await fetch(`${API_URL}/events/${id}`, {
        method: "DELETE",
      });

      if (!response.ok) {
        throw new Error("Delete failed");
      }

      await fetchEvents();
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="event-management">
      <h1>Event Management</h1>

      {error && (
        <div className="error" style={{ color: "red", margin: "10px 0" }}>
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
        <h2>{editingId ? "Edit Event" : "Create New Event"}</h2>

        <div style={{ marginBottom: "10px" }}>
          <label>Event Name: </label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            required
            style={{ padding: "5px" }}
          />
        </div>

        <div style={{ marginBottom: "10px" }}>
          <label>Date: </label>
          <input
            type="datetime-local"
            name="date"
            value={formData.date}
            onChange={handleInputChange}
            required
            style={{ padding: "5px" }}
          />
        </div>

        <div style={{ marginBottom: "10px" }}>
          <label>Location: </label>
          <input
            type="text"
            name="location"
            value={formData.location}
            onChange={handleInputChange}
            required
            style={{ padding: "5px" }}
          />
        </div>

        <button
          type="submit"
          disabled={loading}
          style={{
            padding: "8px 15px",
            marginRight: "10px",
            backgroundColor: "#4CAF50",
            color: "white",
            border: "none",
            borderRadius: "4px",
          }}
        >
          {loading
            ? "Processing..."
            : editingId
            ? "Update Event"
            : "Create Event"}
        </button>

        {editingId && (
          <button
            type="button"
            onClick={() => {
              setFormData({ name: "", date: "", location: "" });
              setEditingId(null);
            }}
            style={{
              padding: "8px 15px",
              backgroundColor: "#f44336",
              color: "white",
              border: "none",
              borderRadius: "4px",
            }}
          >
            Cancel
          </button>
        )}
      </form>

      <div className="events-list">
        <h2>Events</h2>
        {loading && !events.length ? (
          <p>Loading events...</p>
        ) : events.length === 0 ? (
          <p>No events found</p>
        ) : (
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr style={{ backgroundColor: "#f2f2f2" }}>
                <th style={{ padding: "12px", textAlign: "left" }}>Name</th>
                <th style={{ padding: "12px", textAlign: "left" }}>Date</th>
                <th style={{ padding: "12px", textAlign: "left" }}>Location</th>
                <th style={{ padding: "12px", textAlign: "left" }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {events.map((event) => (
                <tr key={event.id} style={{ borderBottom: "1px solid #ddd" }}>
                  <td style={{ padding: "12px" }}>{event.name}</td>
                  <td style={{ padding: "12px" }}>
                    {format(parseISO(event.date), "PPpp")}
                  </td>
                  <td style={{ padding: "12px" }}>{event.location}</td>
                  <td style={{ padding: "12px" }}>
                    <button
                      onClick={() => handleEdit(event)}
                      style={{
                        padding: "5px 10px",
                        marginRight: "5px",
                        backgroundColor: "#2196F3",
                        color: "white",
                        border: "none",
                        borderRadius: "3px",
                      }}
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(event.id)}
                      style={{
                        padding: "5px 10px",
                        backgroundColor: "#f44336",
                        color: "white",
                        border: "none",
                        borderRadius: "3px",
                      }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default EventManagement;
