import React, { useState, useEffect } from "react";
import { format, parseISO } from "date-fns";

const API_URL = "http://localhost:8000";

function EventManagement() {
  const [events, setEvents] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    date: "",
    location: "",
  });
  const [editingId, setEditingId] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchEvents = async () => {
    setLoading(true);
    try {
      const response = await fetch(`${API_URL}/events`);
      if (!response.ok) throw new Error("Failed to fetch events");
      const data = await response.json();
      setEvents(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchEvents();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      // Format date for backend
      const formattedDate = new Date(formData.date).toISOString();

      const response = await fetch(
        editingId ? `${API_URL}/events/${editingId}` : `${API_URL}/events`,
        {
          method: editingId ? "PUT" : "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            name: formData.name,
            date: formattedDate,
            location: formData.location,
          }),
        }
      );

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Request failed");
      }

      // Reset form and refresh events
      setFormData({ name: "", date: "", location: "" });
      setEditingId(null);
      await fetchEvents();
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (event) => {
    // Convert date to local format for input
    const localDate = format(parseISO(event.date), "yyyy-MM-dd'T'HH:mm");
    setFormData({
      name: event.name,
      date: localDate,
      location: event.location,
    });
    setEditingId(event.id);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure?")) return;

    setLoading(true);
    try {
      const response = await fetch(`${API_URL}/events/${id}`, {
        method: "DELETE",
      });

      if (!response.ok) throw new Error("Delete failed");

      await fetchEvents();
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: "800px", margin: "0 auto", padding: "20px" }}>
      <h1>Event Management</h1>

      {error && <div style={{ color: "red", margin: "10px 0" }}>{error}</div>}

      <form
        onSubmit={handleSubmit}
        style={{ marginBottom: "30px", padding: "20px", background: "#f5f5f5" }}
      >
        <h2>{editingId ? "Edit Event" : "Add Event"}</h2>

        <div style={{ marginBottom: "15px" }}>
          <label style={{ display: "block", marginBottom: "5px" }}>
            Event Name:
          </label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            required
            style={{ width: "100%", padding: "8px" }}
          />
        </div>

        <div style={{ marginBottom: "15px" }}>
          <label style={{ display: "block", marginBottom: "5px" }}>Date:</label>
          <input
            type="datetime-local"
            name="date"
            value={formData.date}
            onChange={handleInputChange}
            required
            style={{ width: "100%", padding: "8px" }}
          />
        </div>

        <div style={{ marginBottom: "15px" }}>
          <label style={{ display: "block", marginBottom: "5px" }}>
            Location:
          </label>
          <input
            type="text"
            name="location"
            value={formData.location}
            onChange={handleInputChange}
            required
            style={{ width: "100%", padding: "8px" }}
          />
        </div>

        <button
          type="submit"
          disabled={loading}
          style={{
            padding: "8px 15px",
            marginRight: "10px",
            background: "#4CAF50",
            color: "white",
          }}
        >
          {loading ? "Saving..." : "Save"}
        </button>

        {editingId && (
          <button
            type="button"
            onClick={() => {
              setFormData({ name: "", date: "", location: "" });
              setEditingId(null);
            }}
            style={{
              padding: "8px 15px",
              background: "#f44336",
              color: "white",
            }}
          >
            Cancel
          </button>
        )}
      </form>

      <div>
        <h2>Events</h2>
        {loading && !events.length ? (
          <p>Loading...</p>
        ) : events.length === 0 ? (
          <p>No events found</p>
        ) : (
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr style={{ background: "#f2f2f2" }}>
                <th style={{ padding: "12px", textAlign: "left" }}>Name</th>
                <th style={{ padding: "12px", textAlign: "left" }}>Date</th>
                <th style={{ padding: "12px", textAlign: "left" }}>Location</th>
                <th style={{ padding: "12px", textAlign: "left" }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {events.map((event) => (
                <tr key={event.id} style={{ borderBottom: "1px solid #ddd" }}>
                  <td style={{ padding: "12px" }}>{event.name}</td>
                  <td style={{ padding: "12px" }}>
                    {format(parseISO(event.date), "PPpp")}
                  </td>
                  <td style={{ padding: "12px" }}>{event.location}</td>
                  <td style={{ padding: "12px" }}>
                    <button
                      onClick={() => handleEdit(event)}
                      style={{
                        padding: "5px 10px",
                        marginRight: "5px",
                        background: "#2196F3",
                        color: "white",
                      }}
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(event.id)}
                      style={{
                        padding: "5px 10px",
                        background: "#f44336",
                        color: "white",
                      }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default EventManagement;
