import React, { useState, useEffect } from "react";
import { format, parseISO } from "date-fns";

// In your React component (EventManagement.js)
const API_URL =
  "https://eventhorizon-eufth7a5ambghxef.malaysiawest-01.azurewebsites.net/api";

// Add error handling to your fetch requests:
const fetchEvents = async () => {
  setLoading(true);
  setError("");
  try {
    const response = await fetch(`${API_URL}/events`);

    // First check if response is JSON
    const contentType = response.headers.get("content-type");
    if (!contentType || !contentType.includes("application/json")) {
      const text = await response.text();
      throw new Error(`Expected JSON but got: ${text.substring(0, 100)}`);
    }

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(
        errorData.message || `HTTP error! status: ${response.status}`
      );
    }

    const data = await response.json();
    setEvents(data);
  } catch (err) {
    console.error("Fetch error:", err);
    setError("Failed to load events: " + err.message);
  } finally {
    setLoading(false);
  }
};

function EventManagement() {
  const [events, setEvents] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    date: "",
    location: "",
  });
  const [editingId, setEditingId] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchEvents = async () => {
    setLoading(true);
    setError("");
    try {
      const response = await fetch(`${API_URL}/events`);
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Failed to fetch events");
      }
      const data = await response.json();
      setEvents(data);
    } catch (err) {
      console.error("Fetch error:", err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchEvents();
  }, []);

  /*************  ✨ Windsurf Command ⭐  *************/
  /**
 * Updates the form data state based on user input changes.
 *
 * @param {Object} e - The event object from the input change.
 * @param {string} e.target.name - The name attribute of the input element.
/*******  a030c1a6-f0e8-4a51-9708-087bac88247d  *******/ const handleInputChange =
    (e) => {
      const { name, value } = e.target;
      setFormData((prev) => ({ ...prev, [name]: value }));
    };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      const url = editingId
        ? `${API_URL}/events/${editingId}`
        : `${API_URL}/events`;

      const method = editingId ? "PUT" : "POST";

      // Prepare the payload with proper date formatting
      const payload = {
        name: formData.name.trim(),
        location: formData.location.trim(),
        date: new Date(formData.date).toISOString(),
      };

      // Enhanced fetch request with error handling
      const response = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
        body: JSON.stringify(payload),
      });

      const responseData = await response.json();

      if (!response.ok) {
        throw new Error(
          responseData.error || `Request failed with status ${response.status}`
        );
      }

      // Reset form and refresh events
      setFormData({ name: "", date: "", location: "" });
      setEditingId(null);
      await fetchEvents();
    } catch (err) {
      console.error("API Error:", err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (event) => {
    // Convert the ISO date from server to local datetime format
    const localDate = format(parseISO(event.date), "yyyy-MM-dd'T'HH:mm");
    setFormData({
      name: event.name,
      date: localDate,
      location: event.location,
    });
    setEditingId(event.id);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this event?")) return;

    setLoading(true);
    setError("");
    try {
      const response = await fetch(`${API_URL}/events/${id}`, {
        method: "DELETE",
        headers: {
          Accept: "application/json",
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Delete failed");
      }

      await fetchEvents();
    } catch (err) {
      console.error("Delete error:", err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="event-management">
      <h1>Event Management</h1>

      {error && <div className="error">{error}</div>}

      <form onSubmit={handleSubmit}>
        <h2>{editingId ? "Edit Event" : "Create New Event"}</h2>

        <div className="form-group">
          <label>Event Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            required
            minLength="2"
          />
        </div>

        <div className="form-group">
          <label>Date:</label>
          <input
            type="datetime-local"
            name="date"
            value={formData.date}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Location:</label>
          <input
            type="text"
            name="location"
            value={formData.location}
            onChange={handleInputChange}
            required
            minLength="2"
          />
        </div>

        <button type="submit" disabled={loading}>
          {loading
            ? "Processing..."
            : editingId
            ? "Update Event"
            : "Create Event"}
        </button>

        {editingId && (
          <button
            type="button"
            onClick={() => {
              setFormData({ name: "", date: "", location: "" });
              setEditingId(null);
              setError("");
            }}
            disabled={loading}
          >
            Cancel
          </button>
        )}
      </form>

      <div className="events-list">
        <h2>All Events</h2>

        {loading && !events.length ? (
          <p>Loading events...</p>
        ) : events.length === 0 ? (
          <p>No events found</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Date</th>
                <th>Location</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {events.map((event) => (
                <tr key={event.id}>
                  <td>{event.name}</td>
                  <td>{format(parseISO(event.date), "PPpp")}</td>
                  <td>{event.location}</td>
                  <td>
                    <button
                      onClick={() => handleEdit(event)}
                      disabled={loading}
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(event.id)}
                      disabled={loading}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default EventManagement;
