import React, { useState, useEffect } from "react";
import { format } from "date-fns";
import "./index.css";

const API_URL = "http://localhost:3000";

function App() {
  const [events, setEvents] = useState([]);
  const [attendees, setAttendees] = useState([]);
  const [newEvent, setNewEvent] = useState({
    name: "",
    date: "",
    location: "",
  });
  const [newAttendee, setNewAttendee] = useState({
    name: "",
    email: "",
    event_id: "",
  });
  const [activeTab, setActiveTab] = useState("events");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const res = await fetch(`${API_URL}/events`);
      const contentType = res.headers.get("content-type");
      if (!contentType.includes("application/json")) {
        throw new Error(`Expected JSON but got ${contentType}`);
      }
      // ... rest of your code
    } catch (err) {
      console.error("Fetch error details:", err);
    }
  };

  const handleCreateEvent = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`${API_URL}/events`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newEvent),
      });

      if (!res.ok) {
        const errorData = await res.json(); // Get error details from backend
        throw new Error(errorData.error || "Failed to create event");
      }

      setNewEvent({ name: "", date: "", location: "" });
      fetchData();
    } catch (err) {
      console.error("Creation error details:", err);
      setError(err.message);
    }
  };

  const handleCreateAttendee = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`${API_URL}/attendees`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newAttendee),
      });

      if (!res.ok) throw new Error("Failed to create attendee");

      setNewAttendee({ name: "", email: "", event_id: "" });
      fetchData();
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDeleteEvent = async (id) => {
    if (!window.confirm("Are you sure you want to delete this event?")) return;

    try {
      const res = await fetch(`${API_URL}/events/${id}`, {
        method: "DELETE",
      });

      if (!res.ok) throw new Error("Failed to delete event");

      fetchData();
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDeleteAttendee = async (id) => {
    if (!window.confirm("Are you sure you want to delete this attendee?"))
      return;

    try {
      const res = await fetch(`${API_URL}/attendees/${id}`, {
        method: "DELETE",
      });

      if (!res.ok) throw new Error("Failed to delete attendee");

      fetchData();
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="container">
      <header>
        <h1>EventHorizon Management System</h1>
        <div className="tabs">
          <button
            className={activeTab === "events" ? "active" : ""}
            onClick={() => setActiveTab("events")}
          >
            Events
          </button>
          <button
            className={activeTab === "attendees" ? "active" : ""}
            onClick={() => setActiveTab("attendees")}
          >
            Attendees
          </button>
        </div>
      </header>

      {error && <div className="error">{error}</div>}

      {loading ? (
        <div className="loading">Loading...</div>
      ) : (
        <main>
          {activeTab === "events" ? (
            <>
              <section className="form-section">
                <h2>Create New Event</h2>
                <form onSubmit={handleCreateEvent}>
                  <div className="form-group">
                    <label>Event Name</label>
                    <input
                      type="text"
                      value={newEvent.name}
                      onChange={(e) =>
                        setNewEvent({ ...newEvent, name: e.target.value })
                      }
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Date</label>
                    <input
                      type="datetime-local"
                      value={newEvent.date}
                      onChange={(e) =>
                        setNewEvent({ ...newEvent, date: e.target.value })
                      }
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Location</label>
                    <input
                      type="text"
                      value={newEvent.location}
                      onChange={(e) =>
                        setNewEvent({ ...newEvent, location: e.target.value })
                      }
                      required
                    />
                  </div>
                  <button type="submit">Create Event</button>
                </form>
              </section>

              <section className="list-section">
                <h2>Upcoming Events</h2>
                {events.length === 0 ? (
                  <p>No events found</p>
                ) : (
                  <div className="event-list">
                    {events.map((event) => (
                      <div key={event.id} className="event-card">
                        <div className="event-info">
                          <h3>{event.name}</h3>
                          <p>
                            <strong>Date:</strong>{" "}
                            {format(new Date(event.date), "PPpp")}
                          </p>
                          <p>
                            <strong>Location:</strong> {event.location}
                          </p>
                        </div>
                        <div className="event-actions">
                          <button
                            className="delete-btn"
                            onClick={() => handleDeleteEvent(event.id)}
                          >
                            Delete
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </section>
            </>
          ) : (
            <>
              <section className="form-section">
                <h2>Register New Attendee</h2>
                <form onSubmit={handleCreateAttendee}>
                  <div className="form-group">
                    <label>Full Name</label>
                    <input
                      type="text"
                      value={newAttendee.name}
                      onChange={(e) =>
                        setNewAttendee({ ...newAttendee, name: e.target.value })
                      }
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Email</label>
                    <input
                      type="email"
                      value={newAttendee.email}
                      onChange={(e) =>
                        setNewAttendee({
                          ...newAttendee,
                          email: e.target.value,
                        })
                      }
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Event</label>
                    <select
                      value={newAttendee.event_id}
                      onChange={(e) =>
                        setNewAttendee({
                          ...newAttendee,
                          event_id: e.target.value,
                        })
                      }
                      required
                    >
                      <option value="">Select an event</option>
                      {events.map((event) => (
                        <option key={event.id} value={event.id}>
                          {event.name} ({format(new Date(event.date), "PP")})
                        </option>
                      ))}
                    </select>
                  </div>
                  <button type="submit">Register Attendee</button>
                </form>
              </section>

              <section className="list-section">
                <h2>Attendees</h2>
                {attendees.length === 0 ? (
                  <p>No attendees found</p>
                ) : (
                  <table className="attendees-table">
                    <thead>
                      <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Event</th>
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {attendees.map((attendee) => (
                        <tr key={attendee.id}>
                          <td>{attendee.name}</td>
                          <td>{attendee.email}</td>
                          <td>{attendee.event_name || "Unknown Event"}</td>
                          <td>
                            <button
                              className="delete-btn"
                              onClick={() => handleDeleteAttendee(attendee.id)}
                            >
                              Delete
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                )}
              </section>
            </>
          )}
        </main>
      )}
    </div>
  );
}

export default App;
