import React, { useState, useEffect, useContext } from "react";
import { useAuth } from "../../context/AuthContext";
import { useNavigate } from "react-router-dom";
import { format } from "date-fns";

const API_URL = "http://localhost:8000";

function EventManagement() {
  const { user, logout } = useAuth();
  const history = useHistory();
  const [events, setEvents] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    date: "",
    location: "",
    description: "",
  });
  const [editingId, setEditingId] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!user) {
      history.push("/login");
    } else {
      fetchEvents();
    }
  }, [user, history]);

  const fetchEvents = async () => {
    setLoading(true);
    try {
      const response = await fetch(`${API_URL}/events`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
      const data = await response.json();
      setEvents(data);
    } catch (err) {
      setError("Failed to fetch events");
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const url = editingId
        ? `${API_URL}/events/${editingId}`
        : `${API_URL}/events`;
      const method = editingId ? "PUT" : "POST";

      const response = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) throw new Error("Operation failed");

      setFormData({ name: "", date: "", location: "", description: "" });
      setEditingId(null);
      fetchEvents();
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (event) => {
    setFormData({
      name: event.name,
      date: format(new Date(event.date), "yyyy-MM-dd'T'HH:mm"),
      location: event.location,
      description: event.description || "",
    });
    setEditingId(event.id);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this event?")) return;

    setLoading(true);
    try {
      const response = await fetch(`${API_URL}/events/${id}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });

      if (!response.ok) throw new Error("Delete failed");

      fetchEvents();
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  if (!user) {
    return <div>Redirecting to login...</div>;
  }

  return (
    <div className="event-management">
      <header>
        <h1>EventHorizon Management Portal</h1>
        <div className="user-info">
          <span>
            Welcome, {user.username} ({user.role})
          </span>
          <button onClick={logout}>Logout</button>
        </div>
      </header>

      {error && <div className="error">{error}</div>}

      {/* Event Form - Only show for admin or when editing */}
      {(user.role === "admin" || editingId) && (
        <form onSubmit={handleSubmit}>
          <h2>{editingId ? "Edit Event" : "Create New Event"}</h2>

          <div className="form-group">
            <label>Event Name:</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Date:</label>
            <input
              type="datetime-local"
              name="date"
              value={formData.date}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Location:</label>
            <input
              type="text"
              name="location"
              value={formData.location}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Description:</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleInputChange}
            />
          </div>

          <button type="submit" disabled={loading}>
            {loading
              ? "Processing..."
              : editingId
              ? "Update Event"
              : "Create Event"}
          </button>

          {editingId && (
            <button
              type="button"
              onClick={() => {
                setFormData({
                  name: "",
                  date: "",
                  location: "",
                  description: "",
                });
                setEditingId(null);
              }}
            >
              Cancel
            </button>
          )}
        </form>
      )}

      {/* Events List */}
      <div className="events-list">
        <h2>All Events</h2>

        {loading && !events.length ? (
          <p>Loading events...</p>
        ) : events.length === 0 ? (
          <p>No events found</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Date</th>
                <th>Location</th>
                <th>Description</th>
                <th>Created By</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {events.map((event) => (
                <tr key={event.id}>
                  <td>{event.name}</td>
                  <td>{format(new Date(event.date), "PPpp")}</td>
                  <td>{event.location}</td>
                  <td>{event.description}</td>
                  <td>{event.creator}</td>
                  <td>
                    <button
                      onClick={() => handleEdit(event)}
                      disabled={
                        user.role !== "admin" && user.username !== event.creator
                      }
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(event.id)}
                      disabled={user.role !== "admin"}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default EventManagement;
